import React, { useEffect } from "react";
import {
  ModalOverlay,
  ModalContent,
  CloseButton,
} from "./ModalCertificates.styled";

const ModalCertificates = ({ onClick, modal }) => {
  return (
    <>
      <ModalOverlay onClick={onClick}>
        <ModalContent onClick={(e) => e.stopPropagation()}>
          <CloseButton onClick={onClick}>✖</CloseButton>
          {selectedCertificate &&
          selectedCertificate.contentType?.startsWith("image/") ? (
            <img
              src={`http://localhost:5000/api/certificates/download/${selectedCertificate._id}`}
              alt="Сертифікат"
              style={{ width: "100%" }}
            />
          ) : (
            <iframe
              src={`http://localhost:5000/api/certificates/download/${selectedCertificate._id}`}
              width="100%"
              height="500px"
            />
          )}
        </ModalContent>
      </ModalOverlay>
    </>
  );
};

export default ModalCertificates;
