import { useEffect, useRef } from "react";
import * as pdfjsLib from "pdfjs-dist";
import "pdfjs-dist/build/pdf.worker";

pdfjsLib.GlobalWorkerOptions.workerSrc = new URL(
  "pdfjs-dist/build/pdf.worker.min.js",
  import.meta.url
).toString();

const PdfThumbnail = ({ pdfUrl }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    let isMounted = true; // Додаємо прапорець, щоб перевіряти, чи компонент ще в DOM
    const canvas = canvasRef.current; // Зберігаємо посилання на canvas

    if (!canvas) return;

    const renderPdf = async () => {
      try {
        const loadingTask = pdfjsLib.getDocument(pdfUrl);
        const pdf = await loadingTask.promise;
        const page = await pdf.getPage(1);

        const scale = 1.5;
        const viewport = page.getViewport({ scale });

        const context = canvas.getContext("2d");
        canvas.width = viewport.width;
        canvas.height = viewport.height;

        context.clearRect(0, 0, canvas.width, canvas.height);

        if (!isMounted) return; // Переконуємося, що компонент ще існує

        const renderContext = {
          canvasContext: context,
          viewport: viewport,
        };

        await page.render(renderContext).promise;
      } catch (error) {
        console.error("Помилка при рендері PDF:", error);
      }
    };

    renderPdf();

    return () => {
      isMounted = false; // Якщо компонент розмонтується, більше не рендеримо
      if (canvas) {
        const context = canvas.getContext("2d");
        context.clearRect(0, 0, canvas.width, canvas.height);
      }
    };
  }, [pdfUrl]); // Виконується тільки при зміні `pdfUrl`

  return <canvas ref={canvasRef} />;
};

export default PdfThumbnail;
