import { useState, useEffect } from "react";
import axios from "axios";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";
import {
  CertificatesSection,
  CertificatesTitle,
  CertificatesContainer,
  CertificateItem,
  CertificateTitle,
  CertificateDescription,
  DownloadLink,
} from "./Certificates.styled";

const Certificates = () => {
  const [certificates, setCertificates] = useState([]);
  const [selectedCertificate, setSelectedCertificate] = useState(null);
  useEffect(() => {
    fetchCertificates();
  }, []);

  const fetchCertificates = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5000/api/certificates/list"
      );
      setCertificates(response.data);
    } catch (error) {
      console.error("Помилка при отриманні сертифікатів:", error);
    }
  };

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 p-4">
      {certificates.map((cert) => (
        <div key={cert._id} className="border p-2 rounded-lg shadow">
          <Dialog>
            <DialogTrigger asChild>
              <img
                src={
                  cert.contentType.startsWith("image")
                    ? `http://localhost:5000/api/certificates/download/${cert._id}`
                    : "/pdf-placeholder.png"
                }
                alt="Сертифікат"
                className="w-full h-40 object-cover cursor-pointer"
                onClick={() => setSelectedCertificate(cert)}
              />
            </DialogTrigger>
            <DialogContent>
              <img
                src={`http://localhost:5000/api/certificates/download/${selectedCertificate?._id}`}
                alt="Сертифікат"
                className="w-full h-auto"
              />
              <Button asChild className="mt-2">
                <a
                  href={`http://localhost:5000/api/certificates/download/${selectedCertificate?._id}`}
                  download
                >
                  Завантажити
                </a>
              </Button>
            </DialogContent>
          </Dialog>
        </div>
      ))}
    </div>
  );
};

export default Certificates;
